                requestStream.Write(boundaryBytes, 0, boundaryBytes.Length);
                requestStream.Write(contentDateBytes, 0, contentDateBytes.Length);
            }              

            // Send data
            using (HttpWebResponse response = request.GetResponse() as HttpWebResponse)
            {
