<p>some code:</p>

<pre><code>    #pragma mark - Folder creation - (void)addFolderButtonClicked:(id)sender { UIAlertView *folderNamePrompt = [[UIAlertView alloc] initWithTitle:@"Add Folder" message:@"Name:" delegate:self cancelButtonTitle:@"Cancel" otherButtonTitles:@"OK", nil];

