    {
        NSLog(@"folder items error: %@", error);
    };
    BoxFoldersRequestBuilder *itemBldr = [[BoxFoldersRequestBuilder alloc]initWithQueryStringParameters:@{ @"fields" : @"name,type,id,size,modified_at,created_by,parent,path_collection" }];
    [[BoxSDK sharedSDK].foldersManager folderInfoWithID:folderID requestBuilder:itemBldr success:success failure:failure];
}
</code></pre>
"},{"tags":["powershell","box-api","box"],"owner":{"reputation":3,"user_id":3813433,"user_type":"registered","profile_image":"https://www.gravatar.com/avatar/d7b2e7b9b2a44d1e9d0ce3ba6c365933?s=128&d=identicon&r=PG&f=1","display_name":"whitwo","link":"https://stackoverflow.com/users/3813433/whitwo"},"is_answered":true,"view_count":366,"accepted_answer_id":24633250,"answer_count":1,"score":0,"last_activity_date":1404842207,"creation_date":1404821376,"last_edit_date":1404822305,"question_id":24631514,"link":"https://stackoverflow.com/questions/24631514/set-limit-or-no-limit-to-get-all-groups-in-box","title":"Set limit (or no limit) to Get all Groups in Box","body":"<p>I'm working on a set of PowerShell functions that will be used to manage our enterprise Box implementation.  Right now I'm working on the groups section and can successfully return groups via the 2.0 API.  However, it appears that the call to the groups API has a default limit of 100 - limiting the number of groups that are returned.</p>

