        resultStream.Flush();
        buffer = resultStream.ToArray();
    }

    return buffer;
}

private byte[] GetFormattedBoundary(bool isEndBoundary)
{
    string template = isEndBoundary ? "--{0}--{1}" : "--{0}{1}";
    return Encoding.ASCII.GetBytes(string.Format(template, "AaBbCcX30", Environment.NewLine));
}

